(1, 1, 'Mr WordPress', '', 'https://wordpress.org/', '', '2015-03-03 08:27:16', '2015-03-03 08:27:16', 'Hi, this is a comment.\nTo delete a comment, just log in and view the post&#039;s comments. There you will have the option to edit or delete them.', 0, 'post-trashed', '', '', 0, 0),
(2, 82, 'Brian Gardner', 'brian@briangardner.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:13:54', '2013-09-01 19:13:54', 'This is an example of a comment made on a post. You can either edit the comment, delete the comment or reply to the comment. Use this as a place to respond to the post or to share what you are thinking.', 0, 'post-trashed', '', '', 0, 1),
(3, 82, 'Brian Gardner', 'brian@briangardner.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:04', '2013-09-01 19:14:04', 'This is an example of a nested threaded comment which is new in WordPress 2.7. This is where you can reply to a comment that was previously made, and visually makes reading comments to much easier.', 0, 'post-trashed', '', '', 2, 1),
(4, 82, 'Brian Gardner', 'brian@briangardner.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:11', '2013-09-01 19:14:11', 'This is an example of a comment made on a post. You can either edit the comment, delete the comment or reply to the comment. Use this as a place to respond to the post or to share what you are thinking.', 0, 'post-trashed', '', '', 0, 1),
(5, 82, 'Brian Gardner', 'brian@briangardner.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:19', '2013-09-01 19:14:19', 'This is an example of a nested threaded comment which is new in WordPress 2.7. This is where you can reply to a comment that was previously made, and visually makes reading comments to much easier.', 0, 'post-trashed', '', '', 4, 1),
(6, 82, 'Brian Gardner', 'brian@briangardner.com', 'http://www.briangardner.com/', '76.29.60.137', '2013-09-01 19:14:25', '2013-09-01 19:14:25', 'This is an example of a nested threaded comment which is new in WordPress 2.7. This is where you can reply to a comment that was previously made, and visually makes reading comments to much easier.', 0, 'post-trashed', '', '', 5, 1),
(7, 82, 'Brian Gardner', 'brian@briangardner.com', '', '76.29.60.137', '2013-09-01 19:14:38', '2013-09-01 19:14:38', 'This is an example of a comment made on a post. You can either edit the comment, delete the comment or reply to the comment. Use this as a place to respond to the post or to share what you are thinking.', 0, 'post-trashed', '', '', 0, 1),
(8, 82, 'This is a Sample Trackback', '', 'http://demo.briangardner.com/blog/sample-trackback/', '72.10.51.26', '2013-08-27 00:22:43', '2013-08-27 00:22:43', '[&#8230;] have you seen the sample post with threaded comments over at the Genesis Framework demo [&#8230;]', 0, 'post-trashed', '', 'pingback', 0, 0),
(9, 82, 'Check Out This Sample Trackback', '', 'http://demo.briangardner.com/blog/sample-trackback-2/', '72.10.51.26', '2013-08-27 00:27:38', '2013-08-27 00:27:38', '[&#8230;] another sample post with threaded comments over at the Genesis Framework demo [&#8230;]', 0, 'post-trashed', '', 'pingback', 0, 0);
