INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-03-03 08:27:16', '2015-03-03 08:27:16', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'trash', 'open', 'open', '', 'hello-world', '', '', '2015-09-25 04:21:42', '2015-09-25 04:21:42', '', 0, 'http://laixe.local/?p=1', 0, 'post', '', 1),
(2, 1, '2015-03-03 08:27:16', '2015-03-03 08:27:16', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://laixe.local/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'trash', 'open', 'open', '', 'sample-page', '', '', '2015-09-25 04:14:13', '2015-09-25 04:14:13', '', 0, 'http://laixe.local/?page_id=2', 0, 'page', '', 0),
(5, 1, '2013-09-01 01:52:58', '2013-09-01 01:52:58', '<h1>Headline 1</h1>\n<h2>Headline 2</h2>\n<h3>Headline 3</h3>\n<h4>Headline 4</h4>\n<h5>Headline 5</h5>\n<h6>Headline 6</h6>', 'Sample Post With Headlines', '', 'trash', 'open', 'open', '', 'headlines', '', '', '2015-09-25 04:21:43', '2015-09-25 04:21:43', '', 0, 'http://demo.studiopress.com/genesis/?p=5', 0, 'post', '', 0),
(7, 1, '2015-03-03 08:53:03', '2015-03-03 08:53:03', 'This is an sample category description, which can be used to boost SEO rankings. Make sure you enabled this from the Edit Category screen in your dashboard.', '', '', 'publish', 'open', 'open', '', '7', '', '', '2015-03-03 08:53:03', '2015-03-03 08:53:03', '', 0, 'http://laixe.local/?p=7', 1, 'nav_menu_item', '', 0),
